		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] && !$_REQUEST['Embedded']){
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-success add_new_parent hspacer-md" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus-sign"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode=str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(usuario)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(nombre)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(apellido)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(correo)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(celular)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(cumpleanos)%%>', '', $templateCode);

	// process values
	if($selected_id){
		$templateCode=str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		$templateCode=str_replace('<%%VALUE(usuario)%%>', html_attr($row['usuario']), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(usuario)%%>', urlencode($urow['usuario']), $templateCode);
		$templateCode=str_replace('<%%VALUE(nombre)%%>', html_attr($row['nombre']), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(nombre)%%>', urlencode($urow['nombre']), $templateCode);
		$templateCode=str_replace('<%%VALUE(apellido)%%>', html_attr($row['apellido']), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(apellido)%%>', urlencode($urow['apellido']), $templateCode);
		$templateCode=str_replace('<%%VALUE(correo)%%>', html_attr($row['correo']), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(correo)%%>', urlencode($urow['correo']), $templateCode);
		$templateCode=str_replace('<%%VALUE(celular)%%>', html_attr($row['celular']), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(celular)%%>', urlencode($urow['celular']), $templateCode);
		$templateCode=str_replace('<%%VALUE(cumpleanos)%%>', @date('m/d/Y', @strtotime(html_attr($row['cumpleanos']))), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(cumpleanos)%%>', urlencode(@date('m/d/Y', @strtotime(html_attr($urow['cumpleanos'])))), $templateCode);
	}else{
		$templateCode=str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%VALUE(usuario)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(usuario)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%VALUE(nombre)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(nombre)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%VALUE(apellido)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(apellido)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%VALUE(correo)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(correo)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%VALUE(celular)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(celular)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%VALUE(cumpleanos)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(cumpleanos)%%>', urlencode(''), $templateCode);
	}

	// process translations
	foreach($Translation as $symbol=>$trans){
		$templateCode=str_replace("<%%TRANSLATION($symbol)%%>", $trans, $templateCode);
	}

	// clear scrap
	$templateCode=str_replace('<%%', '<!-- ', $templateCode);
	$templateCode=str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == ''){
		$templateCode .= "\n\n<script>\$j(function(){\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption){
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id){
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	// hook: persona_dv
	if(function_exists('persona_dv')){
		$args=array();
		persona_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
